/**
 * Splits provided array into segments of given size
 * @param {Array} array
 * @param {number} segmentSize
 * @returns {Array}
 */
export function segmentize(array, segmentSize) {
  const segmentsCount = Math.ceil(array.length / segmentSize);
  const returnValue = [];
  for (let i = 0; i < segmentsCount; i += 1) {
    returnValue.push(array.slice(i * segmentSize, (i + 1) * segmentSize));
  }
  return returnValue;
}
